import React from 'react';
import { GlobalContext } from '../../../GlobalContext';
import styles from './AdicionalItem.module.css';

const AdicionalItem = () => {
    const {
        openAdicional,
        setOpenAdicional,
        idAdicional,
        setAdicional,
        addAdicional,
        adicional,
    } = React.useContext(GlobalContext);
