import React from 'react';
import { GlobalContext } from '../../../GlobalContext';
import styles from './AdicionalItem.module.css';

const AdicionalItem = () => {
  const { openAdicional, setOpenAdicional, idAdicional, setAdicional, addAdicional, adicional } = React.useContext(GlobalContext);
  const [checked, setChecked] = React.useState(false);

  function closeAdicionalBox() {    
    setOpenAdicional(!openAdicional);
  }

   const handleChange = () => {
    setChecked(!checked);
  };


  function saveAdicional() {
    addAdicional(idAdicional);
    closeAdicionalBox();
  }

  return (
    <div className={styles.bgObs}>
      <div className={styles.areaObs}>
        <button className={styles.btnClose} onClick={closeAdicionalBox}>x</button>
        <h3>Adicionar Adicionais</h3>
        <div>
      <input        
        value={checked}
        onChange={handleChange}
      />

      <p>Is "Value 1" checked? {checked.toString()}</p>
    </div>
        <textarea type="text" onChange={(e) => setAdicional(e.target.value)} />
        <div className={styles.btnsObs}>
          <button onClick={closeAdicionalBox}>Cancelar</button>
          <button onClick={saveAdicional}>Salvar</button>
        </div>
      </div>
    </div>
  );
};

export default AdicionalItem;
